function main(url) {
    // 如果 url 是对象（如 Axios 响应），尝试提取 URL 字符串
    if (typeof url !== "string") {
        if (url?.data?.url) url = url.data.url; // 可能是 Axios 响应
        else if (url?.url) url = url.url;       // 可能是自定义对象
        else return { app_token: null }; // 或 throw new Error("Invalid URL input")
    }

    // 正则匹配 /base/ 后面的 app_token
    const regex = /\/base\/([a-zA-Z0-9_]+)/;
    const match = url.match(regex);

    // 返回 { app_token: "提取的ID" } 或 { app_token: null }
    return { app_token: match ? match[1] : null };
}

